using namespace std;

int gcd(int a, int b) {
    if( b == 0 ) {
        return a;
    }
    return gcd(b, a % b);
}

int main(int argc, char *argv[]) {
    int t;
    scanf("%d", &t);

    while( t-- ) {
        int n, m, g, l;
        scanf("%d%d", &n, &m);
        g = gcd(n, m);
        l = (n * m) / g;
        printf("%d %d\n", g, l);
    }
    return 0;
}

