ll int array[100010];
ll int dp[100010];

stack<ll int> s;

int main(int argc, char *argv[]) {
    int t;
    scanf("%d", &t);

    while( t-- ) {
        int n;
        scanf("%d", &n);
        for(int i = 0; i < n ; i++ ) {
            scanf("%lld", &array[i]);
        }

        ll int answer = 0;
        dp[0] = 1;

        for( int i = 1 ; i < n ; i++ ) {
            if(array[i] >= array[i-1]) {
