    int t;
    scanf("%d", &t);

    while( t-- ) {
        int n, x;
        scanf("%d", &n);
        for( int i = 0 ; i < n ; i++ ) {
            for( int j = 0 ; j < n ; j++ ) {
                scanf("%d", &x);
                array[x][0] = i;
                array[x][1] = j;
