        int array[110];
        int m, x, y;
        scanf("%d%d%d", &m, &x, &y);

        int p = x * y;

        for(int i = 1 ; i <= 100; i++) {
            array[i] = 0;
        }

        for(int i = 0 ; i < m; i++) {
            scanf("%d", &x);
            array[x] = 1;
