            for(int count = 1; count <= p && (x-count) >= 1; count++) {
                array[x - count] = 1;
            }
        }

        int answer = 0;
        for(int i = 1; i <= 100; i++) {
            answer += !array[i];
        }
        printf("%d\n", answer);
