vector<int> in_order(struct node * root) {
    vector<int> order;
    stack<struct node *> s;
    struct node * p = root;

    int is_done = 0;

    while(is_done == 0) {
        if(p != NULL) {
            s.push(p);
            p = p->left;
        }
        else {
            if(!s.empty()) {
                struct node * t = s.top();
                s.pop();
                order.push_back(t->val);
                p = t->right;
            }
            else {
                is_done = 1;
            }
        }
    }
    return order;
}

