int numTrees(int n) {
    int result = 0;
    int * count = (int *) calloc(n+1, sizeof(int));

    count[0] = 1;
    count[1] = 1;

    for(int i = 2; i <= n; i++) {
        for(int j = 0; j < i; j++) {
            count[i] += count[j] * count[i - j - 1];
        }
    }
    result = count[n];
    free(count);

    return result;
}

int main(int argc, char *argv[]) {
    int n;
    scanf("%d", &n);
    printf("%d\n", numTrees(n));
    return 0;
