vector< vector<int> > levelOrder(struct node * root) {
    vector< vector<int> > v;

    if(root == NULL) {
        return v;
    }

    queue<struct node *> q;

    q.push(root);
    while(!q.empty()) {
        vector<int> level_vector;
        int s = q.size();

        while(s--) {
            struct node * t = q.front();
            q.pop();

            level_vector.push_back(t->val);

            if(t->left) { q.push(t->left); }
            if(t->right) { q.push(t->right); }
        }
        v.push_back(level_vector);
    }

    return v;
}

