    int l = _is_valid_bst(root->left, m);

    if(root->val <= *m) {
        return 0;
    }
    else {
        *m = root->val;
    }

    int r = _is_valid_bst(root->right, m);

    return l && r;
}

int is_valid_bst(struct node * root) {
    long long int m = (long long int) INT_MIN - 1;
    return _is_valid_bst(root, &m);
}

