bool isPalindrome(struct node * head) {

    if(head == NULL || head->next == NULL) {
        return 1;
    }

    int is_palin = 1;
    struct node * p = head;
    struct node * mid = head;
    struct node * prev = head;

    while(p && p->next) {
        prev = mid;
        mid = mid->next;
        p = p->next->next;
    }

    prev->next = NULL;
    mid = reverse_list(mid);

    struct node * a = head;
    struct node * b = mid;

    while(a) {
        if(a->val != b->val) {
            is_palin = 0;
            break;
        }
        a = a->next;
        b = b->next;
    }

    mid = reverse_list(mid);
    prev->next = mid;

    return is_palin;
}

