struct node * reverse_list(struct node * head, int m, int n) {

    if(m == n) {
        return head;
    }

    struct node * start = head, * end = NULL;
    struct node * temp = head;
    struct node * prev = NULL, * next = NULL;

    temp = head;
    for(int i = 1 ; i < m; i++) {
        prev = temp;
        temp = temp->next;
    }
    start = temp;

    temp = head;
    for(int i = 1; i < n; i++) {
        temp = temp->next;
        next = temp->next;
    }
    end = temp;

    end->next = NULL;


    struct node * p = start;
    struct node * q = next;
    struct node * r = NULL;

    while(p) {
        r = p->next;
        p->next = q;
        q = p;
        p = r;
    }

    if(prev) {
        prev->next = q;
    }
    else {
        head = q;
    }

    return head;
}

