void flatten(struct TreeNode* root) {
    struct TreeNode *node = root;
    while(node) {
        if(node->left) {
            struct TreeNode *temp = node->left;
            while(temp->right) {
                temp = temp->right;
            }
            // temp is the rightmost in the left subtree
            temp->right = node->right;
            node->right = node->left;
            node->left = NULL;
        }
        node = node->right;
    }
}

