struct node * getIntersectionNode(struct node * headA, struct node * headB) {
    struct node * a = headA;
    struct node * b = headB;

    int len_a = 0, len_b = 0;

    while(a) { len_a ++; a = a->next; }
    while(b) { len_b ++; b = b->next; }

    if(len_a < len_b) {
        struct node * temp = headA;
        headA = headB;
        headB = temp;

        int t = len_a;
        len_a = len_b;
        len_b = t;
    }

    a = headA; b = headB;
    for(int i = 0 ; i < (len_a - len_b); i++) {
        a = a->next;
    }

    struct node * result = NULL;
    while(a && b) {

        if(a == b) {
            result = a;
            break;
        }

        a = a->next;
        b = b->next;
    }

    return result;
}

