	switch ( c ) {
		case '(':
			return 1;
		case '+':
		case '-':
			return 2;
		case '*':
		case '/':
		case '^':
			return 3;
	}
	return 0;

}

int isop ( char c1 ) {
	if ( c1 == '(' || c1 == '+' || c1 == '-' || c1 == '*' || c1 == '/' || c1 == '^' )
		return 1;
	return 0;
}

void print(stack<char> &s) {
	if(s.empty()) {
       		cout << endl;
        	return;
    	}

	char x= s.top();
    	s.pop();
    	print(s);
    	s.push(x);
    	cout << x << " ";
}

