	getchar();
	while ( t-- ) {	

		getline ( cin , infix );

		for ( int i = 0 ; i < infix.length() ; i++ ) {
			char ch = infix[i];
	
			if ( ch == '(' ) {
				s.push ( ch );
			}
			else if ( isalnum ( ch ) ) {
					cout << ch;
			}
			else if ( ch == ')' ) {
				while ( s.top() != '(' ) {
					cout << s.top();
					s.pop();
				}
				s.pop();
			}
			else if ( isop( ch ) ) {
	
				while ( !s.empty() && precedence ( s.top() ) >= precedence ( ch ) ) {
