		int total_flow = 0;
		int net_flow = 0;

		int i , j;
		for ( i = 0 ; i < t ; i++ ) {
			for ( j = 0 ; j < t ; j++ ) {
				scanf("%d" , &array[i][j] );
				total_flow += (array[i][j]);
