#define SIZE 1000000
int array[SIZE];

int find_candidate ( int n ) {

	int index , i;
	int count = 1;

	index = 0;
	for ( i = 1 ; i < n ; i++ ) {
		if ( array[index] == array[i] ) {
			count ++;
		}
		else {
			count --;
		}

		if ( count == 0 ) {
			index = i;
			count = 1;
		}
	}
	return array[index];
}

int main ( int argc , char * argv[] ) {

	int t , z , i;
	scanf("%d" , &t);
	for ( z = 1 ; z <= t ; z++ ) {
