		for ( i = 0 ; i < n ; i++ ) {
			scanf("%d" , &array[i]);
		}

		int count = 0;
		int candidate = find_candidate(n);

		for ( i = 0 ; i < n ; i++ ) {
			if ( array[i] == candidate ) {
				count++;
			}
		}

		if ( count > n/2 ) {
			printf("YES %d\n" , candidate);
		}
		else {
			printf("NO\n");
		}

	}
