		int d = -1;	// difference

		int n, k;
		int flag = 0;

		scanf("%d%d", &n, &k);

		int temp, temp_old;

		for (int i = 0; i < n; i++) {

			scanf("%d", &temp);

			if (i != 0) {
				d = temp - temp_old;

				if (flag == 0)
					flag = d;

				diff.push_back(d);

			}

			if (i == n - 1) {
				last_val.push_back(temp);
