#define ASIZE 10

struct minheapcomparator {
	bool operator()(int i, int j) {
		return i > j;
	}
};

priority_queue<int> l;
priority_queue<int, vector<int>, minheapcomparator> r;

int check_size() {
	if (l.size() == r.size())
		return 0;
	if (l.size() > r.size()) {
		return -1;
	} else {
		return 1;
	}
}

void get_median(int &med) {

	int c = check_size();

	switch (c) {

	case 0:

		med = l.top();
		l.pop();
		break;

	case -1:

		med = l.top();
		l.pop();

		break;
	case 1:

		med = r.top();
		r.pop();

		break;
	}

}

void median(int current_element, int &med) {

	int c = check_size();

	switch (c) {
	case 0:

		if (current_element < med) {
			l.push(current_element);
			med = l.top();
		} else {
			r.push(current_element);
			med = r.top();
		}

		break;
	case -1:

		if (current_element < med) {
			int t = l.top();
			l.pop();
			r.push(t);

			l.push(current_element);

			med = l.top();
