int absurdity(int n1) {

	int n = n1;
	int val = 1;

	while (n != 0) {
		if (n % 10 == 0) {
			n = n / 10;
		} else
			break;
	}

	int d = n % 10;

	int a = 0;
	while (n != 0) {
		a++;
		n = n / 10;
