int lca ( int a , int b , int * height , int * parent , int n ) {

	if ( height[a] > height[b] ) {
		int t;
		t = a;
		a = b;
		b = t;
	}

	int pa = a , pb = b;

	while ( height[pa] != height[pb] ) {
		pb = parent[pb];
	}

	if ( pa == pb )
		return pa;

	while ( parent[pa] != parent[pb] ) {
		pa = parent[pa];
		pb = parent[pb];
	}

	if ( parent[pa] == 0 )
		return pa;
	else
		return parent[pa];
}

