int readline(char * str) {

	int i = 0;
	char ch;
	while ((ch = getchar()) != '\n') {
		str[i++] = ch;
	}
	str[i] = '\0';
	return i;
}

int rank[51];
int mult[51];

int main(int argc, char * argv[]) {

	int t;
	int array[51];

	scanf("%d", &t);
	while (t--) {

		priority_queue<int, std::vector<int>, std::greater<int> > q;
		int count = 0;
		int p, l;

		scanf("%d%d", &p, &l);

		for (int i = 0; i < l; i++) {
			scanf("%d", &rank[i]);
			array[i] = rank[i];
			mult[i] = 1;

			q.push(rank[i]);
		}

		int time = 0;

		while (1) {
			//cout << "IN LOOP : count " << count << " and p = " << p << endl;

			time = q.top();
			q.pop();

			while (!q.empty() && q.top() == time)
				q.pop();

			for (int i = 0; i < l; i++) {
				if (array[i] == time) {
					count++;
					mult[i]++;
					array[i] = array[i] + rank[i] * mult[i];
					q.push(array[i]);
				}
			}

			if (count >= p)
				break;

		}

		printf("%d\n", time);

	}
	return 0;
}
