				//cout << "result = " << result << endl;
				suma = sumb = 0;
				i++;
				j++;
			}

			if ( i >= n && j >= m )
				break;

			if ( i >= n ) {
				//cout << "adding remaining b" << endl;
				while ( j < m ) {
					sumb += b[j];
					j++;
				}
				result += max(suma , sumb);
				break;
			}

			if ( j >= m ) {
				//cout << "adding remaining a" << endl;
				while ( i < n ) {
					suma += a[i];
					//cout << "suma = " << suma << endl;
					i++;
				}
				//cout << " max of : " << suma << " and " << sumb << endl;
				result += max(suma, sumb);
				//cout << "result = " << result << endl;
				break;
			}

		}

		cout << result << endl;

		scanf("%d" , &n);
	}

	return 0;
}
