using namespace std;

map<int,int> prime_factors( long long int n ) {
	map<int,int> fact;

	while ( n % 2 == 0 ) {
		if ( fact.count(2) == 0 ) {
			fact[2] = 0;
		}
		fact[2]++;
		n /= 2;
	}

	for ( int i = 3; i <= sqrt(n) ; i+=2 ) {
		while ( n % i == 0 ) {
			if ( fact.count(i) == 0 ) {
				fact[i] = 0;
			}
			fact[i]++;
			n /= i;
		}
	}

	if ( n > 2 ) {
		if ( fact.count(n) == 0 ) {
			fact[n] = 0;
		}
		fact[n]++;
	}

	return fact;
}

