ll int array[100001];

ll int hist(int t) {

	stack<ll int> s;
	ll int maxa = 0, tp, area;

	int i = 0;

	while (i < t) {
		if (s.empty() || array[i] >= array[s.top()]) {
			s.push(i++);
		} else {
			tp = s.top();
			s.pop();

			area = array[tp] * (s.empty() ? i : i - s.top() - 1);

			if (area > maxa)
				maxa = area;

		}
	}

	while (!s.empty()) {
		tp = s.top();
		s.pop();

		area = array[tp] * (s.empty() ? i : i - s.top() - 1);
		if (area > maxa)
			maxa = area;
	}

	return maxa;
}

int main(int argc, char * argv[]) {

	int t;
	scanf("%d", &t);
	while (t != 0) {

		for (int i = 0; i < t; i++) {
			scanf("%lld", &array[i]);
		}

		printf("%lld\n", hist(t));

		scanf("%d", &t);
	}
	return 0;
