        count += map_c[arr[i]];

        // number is a B
        // add number of Bs that exist into into Cs
        map_c[arr[i] * r] += map_b[arr[i]];

        // number is an A
        // this means add the count of it being B
        map_b[arr[i] * r]++;
    }

    printf("%lld\n", count);
