ll int count(int sum, int n) {
    if(sum < 0) {
        return 0;
    }

    if(n < 0 && sum >= 1) {
        return 0;
    }

    if(dp[sum][n] != -1) {
        return dp[sum][n];
    }

    if(sum == 0) {
        dp[sum][n] = 1;
        return 1;
    }

    dp[sum][n] = count(sum, n - 1) + count(sum - arr[n], n);

    return dp[sum][n];
}

