void reverse_array(int * array, int length) {
    int start = 0, end = length - 1;
    while(start < end) {
        int t = array[start];
        array[start] = array[end];
        array[end] = t;
        start++;
        end--;
    }
}

void print_array(int * array, int length) {
    for(int i = 0; i< length; i++) {
        printf("%d ", array[i]);
    }
    printf("\n");
}

