        scanf("%d", &array[i]);
    }

    k = k % n; // in case rotations are more than n

    while(q--) {
        int x;
        scanf("%d", &x);

        if(x - k >= 0) {
            printf("%d\n", array[x - k]);
        }
        else {
            printf("%d\n", array[n + x - k]);
        }

    }

    return 0;
