int FindMergeNode(Node * headA, Node * headB) {
    int lp = 0, lq = 0;
    struct Node * p = headA;
    struct Node * q = headB;

    while(p) {
        lp++;
        p = p->next;
    }

    while(q) {
        lq++;
        q = q->next;
    }

    p = headA; q = headB;
    if(lp > lq) {
        struct Node * t = p;
        p = q;
        q = t;

        int tt = lp;
        lp = lq;
        lq = tt;
    }

    for(int i = 0 ; i < (lq-lp); i++) {
        q = q->next;
    }

    while(p && q) {
        if(p == q) {
            return p->data;
        }
        p = p->next;
        q = q->next;
    }
    return 0;
}

int main(int argc, char *argv[]) {
    struct Node * head = new_node(1);
    head->next = new_node(2);
    head->next->next = new_node(3);
    cout << FindMergeNode(head) << endl;
    return 0;
