int arr1[100001];
int arr2[100001];

int foo(int n, int m, ll int x) {
    ll int sum = 0;
    int i = 0, j = 0;
    int count = 0;

    while (i < n) {
        sum += (ll int) arr1[i];
        i++;
        if (sum > x) {
            i--;
            sum -= (ll int) arr1[i];
            break;
        }
