        sum += (ll int) arr2[j];
        j++;
        while (sum > x && i > 0) {
            i--;
            sum -= (ll int) arr1[i];
        }
        count = (sum <= x) ? MAX( (count), (i + j) ) : count;
    }

    return count;
}

