        if(s.empty()) {
            s.push(str[i]);
            continue;
        }

        if(s.top() == str[i]) {
            s.pop();
            continue;
        }

        s.push(str[i]);
    }

    if(s.empty()) {
        printf("Empty String\n");
        return 0;
    }

    stack<char> temp;
    while(!s.empty()) {
        temp.push(s.top());
        s.pop();
    }

    while(!temp.empty()) {
        printf("%c", temp.top());
        temp.pop();
    }
    printf("\n");
