struct Node * RemoveDuplicates(struct Node * head) {
    if(head == NULL) {
        return head;
    }

    struct Node * p = head;
    struct Node * first = p;

    int val = p->data;
    p = p->next;

    while(p) {
        if(p->data != val) {
            val = p->data;
            first->next = p;
            first = first->next;
        }
        p = p->next;
    }
    first->next = NULL;
    return head;
}

