    }

    chances -= abs(lt - prefix_length);
    chances -= abs(ls - prefix_length);

    if(chances < 0) {
        printf("No\n");
        return 0;
    }

    if(chances % 2 == 0) {
        printf("Yes\n");
        return 0;
    }

    printf("No\n");
    return 0;
}


